        var range = new { Value = rangeIndex };
        staticIps.Add(new Aiven.StaticIp($"static_ips-{range.Value}", new()
        {
            Project = projectId,
            CloudName = region,
        }));
    }
    var @default = new Aiven.Pg("default", new()
    {
        ServiceName = "postgres",
        Project = aivenProjectId,
        ProjectVpcId = aivenProjectVpcId,
